import Link from 'next/link'
import Head from 'next/head'
import Router from 'next/router'

export default function MainLAyout({children}) {
    
 
   
  return (


    <div className="acontainer">
        <Head>
            <title>Create Next App</title>
            <meta name="viewport" content="width=device-width, initial-scale=1"/>
            <link rel="stylesheet" href="path/to/font-awesome/css/font-awesome.min.css"></link>
            <link rel="icon" href="/favicon.ico" />
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
            <link rel="stylesheet"href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css"/>
            <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
            <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.16.0/umd/popper.min.js"></script>
            <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/js/bootstrap.min.js"></script>
            
        </Head>

        
        <div class="sidebar">
        <div className="logo"><a href="#home">Logo</a></div>
        <Link href={'/index'}><a>Settings</a></Link>
        <Link href={'/orders'}><a>Orders</a></Link>
        <Link href={'/clients'}><a>Clients</a></Link>
        <Link href={'/products'}><a>Products</a></Link>
        <Link href={'/import'}><a>Import</a></Link>
        <Link href={'/delivery'}><a>Delivery</a></Link>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
        <a href="#about">About</a>
        </div>
  
        <main>
        {children}
        </main>
      
           


        <style jsx global>{`

            .acontainer {
            left:0;
            padding:0;
            margin:0;
            }
            
          
            .sidebar {
                margin: 0;
                padding: 0;
                width: 200px;
                background-color: #f1f1f1;
                position: fixed;
                height: 100%;
                overflow: auto;
              }
              
              .sidebar a {
                display: block;
                color: black ;
                padding: 16px;
                text-decoration: none;
              }
               
              .sidebar a.active {
                background-color: #4CAF50;
                color: white;
              }
              
              .sidebar a:hover:not(.active) {
                background-color: #555;
                color: white;
              }
              
              @media screen and (max-width: 700px) {
                .sidebar {
                  width: 100%;
                  height: auto;
                  position: relative;
                }
                .sidebar a {text-align: center;
                  float: none;
                  }
                div.homeContainer {margin-left: 0;}
              }
              
              @media screen and (max-width: 400px) {
                .sidebar a {
                  width:100%
                  text-align: center;
                  float: none;
                }
              }

           
        `}</style>

        <style jsx global>{`
               .homeContainer {
                min-height: 100vh;
                padding-left: 15rem ;
                display: flex;
                flex-direction: column;
                
                
                
              }
              @media screen and (max-width: 700px){
              .homeContainer{
                padding-left:10px;
      
              }
              }
            html,
            body {
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
                Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
                sans-serif;
            }

            * {
            box-sizing: border-box;
            }
        `}</style>
    </div>

  )
}
